package conditionals;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sayi = 18;
		if(sayi<15) {
			System.out.println("Sayımız 15den küçük");
		}
		else if(sayi==20) {
			System.out.println("Sayımız 20");
		}
		else {
			System.out.println("Sayımız 15den büyük");
		}

	}

}
